var jade = require('jade');
var fs = require('fs');

var __coursedir = "./courses/";
var __viewdir = "./views/";

function render() {

  var file = fs.readFileSync(__coursedir + 'course001.json');

  var courseConfig = JSON.parse(file);

  var html = jade.renderFile(__viewdir + './course.jade', {courseConfig:courseConfig});

  return html;
}

exports.render = render;
